                i=0
                xarr=np.zeros(satlength)
                yarr=np.zeros(satlength)
                ATST=np.zeros(satlength)
                for i in range (0,satlength):
                        st2=str(satdf.loc[i,"LONG"])
                        if st2.find('E')!=-1:
                                lat=st2.replace('E','')
                                imx=384*(1+(float(lat)/180))
                                xarr[i]=int(imx)
                        elif st2.find('W')!=-1:
                                lat=st2.replace('W','')
                                imx=384*(1-(float(lat)/180))
                                xarr[i]=int(imx)
                        st3=str(satdf.loc[i,"LAT"])
                        if st3.find('S')!=-1:
                                lon=st3.replace('S','')
                                imy=205*(1+(float(lon)/90))
                                yarr[i]=int(imy)
                        elif st3.find('N')!=-1:
                                lon=st3.replace('N','')
                                imy=205*(1-(float(lon)/90))
                                yarr[i]=int(imy)
                        ATST[i]=str(satdf.loc[i,"AT"])
                        #print (imx)
                        i+=1
                #plot track
                Canvas.plot(self,xarr,yarr)
                #plot sat position
                endnum=0
                while True:
                    Datetime=datetime.datetime.now()
